import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Produtos.css';

const Produtos = () => {
  const [produtos, setProdutos] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3001/produtos')
      .then((res) => setProdutos(res.data))
      .catch((err) => console.error('Erro ao carregar produtos:', err));
  }, []);

  return (
    <div className="produtos-container">
      <h1 className="titulo">Nossos Produtos</h1>
      <div className="produtos-grid">
        {produtos.map((produto) => (
          <div key={produto.id} className="produto-card">
            <img
              src={getImagemProduto(produto.nome)}
              alt={produto.nome}
              className="produto-imagem"
            />
            <h2>{produto.nome}</h2>
            <p>R$ {produto.preco.toFixed(2)}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

const getImagemProduto = (nome) => {
  if (nome.includes('Original')) return 'https://media.giphy.com/media/SWoSkN6DxTszqIKEqv/giphy.gif';
  if (nome.includes('White')) return 'https://media.giphy.com/media/qgQUggAC3Pfv687qPC/giphy.gif';
  if (nome.includes('Mango')) return 'https://media.giphy.com/media/3oKIPwoeGErMmaI43C/giphy.gif';
  if (nome.includes('Pipeline')) return 'https://media.giphy.com/media/3oKIPj8bW3F1S6lW1C/giphy.gif';
  if (nome.includes('Zero')) return 'https://media.giphy.com/media/l0ExvMqE1V8fYZpmY/giphy.gif';
  return 'https://tenor.com/pt-BR/view/monster-drink-monster-energy-drink-energy-drink-gif-9759446916220461645';
};

export default Produtos;
